import styles from './Basket.module.scss';
import { useState, useContext } from "react";
import BasketDetails from "../basket.Details/BasketDetails";
import { CartContext } from "../../App";

function Basket() {
  const [cart] = useContext(CartContext);
  const [isDetailsVisible, setIsDetailsVisible] = useState(false);
  const [orderCount, setOrderCount] = useState(0); // Dodany stan orderCount

  const toggleDetails = () => {
    if (isDetailsVisible) {
      closedetails();
    } else {
      showdetails();
    }
  };

  const showdetails = () => {
    setIsDetailsVisible(true);
  };

  const closedetails = () => {
    setIsDetailsVisible(false);
  };

  const handleOrderClick = () => {
    setOrderCount(orderCount + 1); // Zwiększanie orderCount przy każdym kliknięciu w zamów
  };

  return (
    <div>
      <div className={styles.cartContent}>
        <div onClick={toggleDetails}>
          <img className={styles.basketLogo} alt="" src="/logo192.png" />
          <span className={styles.counter}>{Object.keys(cart).length}</span> {/* Wyświetlanie liczby elementów w koszyku */}
        </div>
        <button onClick={handleOrderClick}>Zamów</button> {/* Przycisk "Zamów" */}
      </div>
      {isDetailsVisible && <BasketDetails handleClose={closedetails} />}
      <div>Liczba zamówień: {orderCount}</div> {/* Wyświetlanie liczby zamówień */}
    </div>
  );
}

export default Basket;
