import styles from './Basket.module.scss'
import { useState, useContext } from "react";
import BasketDetails from "../basket.Details/BasketDetails";
import { CartContext } from "../../App";

function Basket() {
  const [cart] = useContext(CartContext);
  const [isDetailsVisible, setIsDetailsVisible] = useState(false);
  const [clickCount, setClickCount] = useState(0); // Dodany stan clickCount

  const toggleDetails = () => {
    if (isDetailsVisible) {
      closedetails();
    } else {
      showdetails();
    }
    setClickCount(clickCount + 1); // Inkrementacja clickCount przy każdym kliknięciu
  };

  const showdetails = () => {
    setIsDetailsVisible(true);
  };

  const closedetails = () => {
    setIsDetailsVisible(false);
  };

  return (
    <div>
      <div className={styles.cartContent}>
        <div onClick={toggleDetails}>
          <img className={styles.basketLogo} alt="" src="/logo192.png" />
          <span className={styles.counter}>{clickCount}</span> {/* Wyświetlanie clickCount */}
        </div>
      </div>
      {isDetailsVisible && <BasketDetails handleClose={closedetails} />}
    </div>
  );
}

export default Basket;
