import React, { useState } from "react";

function Order() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault(); // Zapobiegamy domyślnej akcji formularza

    // Wyświetlamy wartości na konsoli
    console.log("Imię: " + name);
    console.log("Email: " + email);
    console.log("Wiadomość: " + message);

    // Tutaj możesz dodać kod do wysłania danych na serwer
    // np. używając biblioteki fetch lub AJAX

    // Czyścimy pola formularza
    setName("");
    setEmail("");
    setMessage("");

    // Możemy również wyświetlić komunikat potwierdzający wysłanie formularza
    alert("Wiadomość została wysłana!");
  };

  return (
    <div>
      <h2>Order</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Imię:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <label htmlFor="message">Wiadomość:</label>
        <textarea
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          required
        ></textarea>

        <button type="submit">Wyślij</button>
      </form>
    </div>
  );
}

export default Order;
